// Prepare table data
let employeeTable = {
  "columns": ["Name", "Department", "Position"],
  "rows": [
    {"Name": "John Smith", "Department": "Sales", "Position": "Manager"},
    {"Name": "Jane Doe", "Department": "Marketing", "Position": "Director"},
    {"Name": "Bob Johnson", "Department": "IT", "Position": "Developer"}
  ]
};

// Add to Word document
// File Descriptor: word_fd
// Table: employeeTable
// Space After: 12
// Headers: true

// result array (page content)
[
  "This is the content of page 1...",
  "This is the content of page 2...",
  "This is the content of page 3..."
]

// header_footer array (when Get Header and Footer is enabled)
[
  {
    "header": "Company Report 2024",
    "footer": "Page 1 - Confidential"
  },
  {
    "header": "Company Report 2024",
    "footer": "Page 2 - Confidential"
  }
]
